        require(tokenSupply[_id] + _quantity <= tokenMaxSupply[_id], 'ERC1155Tradable: Max supply reached');
        tokenSupply[_id] = tokenSupply[_id] + _quantity;
        _mint(_to, _id, _quantity, _data);
